    public static void heapMe(int[] arr) {
        int len = arr.length-1;
        while(len > 0){
            heapSort(arr, len);
            swap(arr, 0, len);
            len--;
        }
    }
    public static void heapSort(int[] arr, int last){
        int cur = last;
        // 这样调整堆 效率太差了，调用 n-1 次，每次都要遍历一遍，相当于 n^2
        while(cur > 0){
            int father = (cur - 1)/2;
            if(arr[cur] > arr[father]){
                swap(arr, father, cur);
            }
            cur--;
        }
    }



